const dataFromAPI = ()=>{
    axios.get("https://api.open-meteo.com/v1/forecast?latitude=32.814&longitude=-96.9489&current=temperature_2m,relative_humidity_2m,apparent_temperature,is_day,weather_code,surface_pressure,wind_speed_10m,wind_direction_10m&temperature_unit=fahrenheit&wind_speed_unit=ms&precipitation_unit=inch")
    .then(res=>{
        const currentTemp = res.data.current.temperature_2m
        const tempUnit = res.data.current_units.temperature_2m
        document.querySelector('.temp').innerHTML = currentTemp +' '+ tempUnit
    
        const humidity = res.data.current.relative_humidity_2m
        const humidUnit = res.data.current_units.relative_humidity_2m
        document.querySelector('.humid').innerHTML = humidity+' '+humidUnit
    
        const appTemp = res.data.current.apparent_temperature
        document.querySelector('.apTemp').innerHTML = appTemp +' '+tempUnit
    
        const surPress = res.data.current.surface_pressure
        const pressUnit = res.data.current_units.surface_pressure
        document.querySelector('.surPress').innerHTML = surPress+' '+pressUnit
    
        const windSpeed = res.data.current.wind_speed_10m
        const windUnit = res.data.current_units.wind_speed_10m
        document.querySelector('.windSpeed').innerHTML = windSpeed+' '+windUnit
    
        const windDirect = res.data.current.wind_direction_10m
        const windDirectUnit = res.data.current_units.wind_direction_10m
        document.querySelector('.windDirect').innerHTML = windDirect+' '+windDirectUnit
    })  
} 

dataFromAPI()

setInterval(() => {
	  dataFromAPI()
}, 10000);



