const DateAndTimeFromAPI = ()=>{
    axios.get("https://worldtimeapi.org/api/timezone/America/North_Dakota/Center")
.then(res=>{
    // console.log(res)
    const dateToday = res.data.datetime
    let dateTodaySliced = dateToday.slice(0,10)
    document.querySelector('.dateToday').innerHTML = dateTodaySliced
    
    let daysOfTheWeek = ["Monday","Tuesday", "Wednesday","Thursday","Friday", "Saturday", "Sunday"]
    let todayDay = res.data.day_of_week;
    let todayDayInWords = daysOfTheWeek[todayDay-1]
    document.querySelector('.day').innerHTML = todayDayInWords

    let allDate = Date()
    let hoursAndMinutes = allDate.slice(16,24)

    timeToArray = hoursAndMinutes.split(':').map(Number);
    let hoursInTime = timeToArray[0];
    let minutesInTime = timeToArray[1];
    let secondsInTime = timeToArray[2];
    let amOrpm = 'AM'


    if(hoursInTime>12){
        hoursInTime-=12;
        amOrpm = "PM"
    }

    if(secondsInTime <10){
        secondsInTime= "0"+secondsInTime
    }
    if(minutesInTime <10){
        minutesInTime= "0"+minutesInTime
    }

    if(hoursInTime <10){
        hoursInTime= "0"+hoursInTime
    }
    
    let timeToShow = hoursInTime+':'+minutesInTime+':'+secondsInTime+" "+amOrpm
    document.querySelector('.time').innerHTML = timeToShow
})
}

DateAndTimeFromAPI()
setInterval(()=>{
    DateAndTimeFromAPI()
},1000)


